var createVertex = require("./create_vertex");

function createSupplementaryPointsForCircle(geojson) {
  var properties = geojson.properties,
      geometry = geojson.geometry;
  if (!properties.user_isCircle) return null;
  var supplementaryPoints = [];
  var vertices = geometry.coordinates[0].slice(0, -1);

  for (var index = 0; index < vertices.length; index += Math.round(vertices.length / 4)) {
    supplementaryPoints.push(createVertex(properties.id, vertices[index], "0.".concat(index), false));
  }

  return supplementaryPoints;
}

module.exports = createSupplementaryPointsForCircle;
